%Interpolation%Newton Forward Differenceclc;clear all;format long;printf("Newton Forward Difference\n");%load nodes.txt %to load (x_i,y_i) from a txt file. Comment if not being used.%x=nodes;dd(1,1)=input("Enter the x0: "); % To approximate the function f(x) in an interval. Comment if not being used.n=input("\n Enter total number of points:");h=input("\n Enter the finite difference h:"); for i=2:n dd(i,1)=dd(i-1,1)+h;endfor  dd(1:n,2)=f(dd(1:n,1));for j=1:n-1;  for i=1:n-j;    dd(i,j+2)=(dd(i+1,j+1)-dd(i,j+1));  endforendforddp=[dd(1,n+1)];for k=n-1:-1:1;  d(1,1:n-k)=0;  d(1,n-k+1)=dd(1,k+1);  p=conv(p,[1,-k])/(k+1)+d;endforpolyout(p,'t')printf("\n Here t=(x-x0)/h\n");