%Interpolation%Lagrange Interpolation Methodclc;clear all;format long;printf("Lagrange Interpolation Method\n");load nodes.txt %to load (x_i,y_i) from a txt file. Comment if not being used.x=nodes;%x=input("Enter the nodes \n(like, [a,b,c,d] where a,b,c,d are nodes):\n "); % To approximate the function f(x) in an interval. Comment if not being used.n=length(x); y=x(1:n,2); %For nodes from a text file%y=f(x'); %For approximating a functionp(1:n)=0;for i=1:n  l=[1];  for j=1:n    if (j==i)      l=l;    else      l=conv(l,[1,-x(j,1)]/(x(i,1)-x(j,1)));    endif  endfor  p=p+l*y(i);endforpolyout(p,'t')