%Root Finding%Bisection Methodclc;clear all;format long;e=10.^(-14);printf("Bisection Method\n");a=input("enter a\n") %lower bound of the interval [a,b]b=input("enter b\n") %upper bound of the interval [a,b]c=(a+b)/2;if(abs(b-c)<=e)  b  break;endifn=input("number of iterations\n") %Number of iterationsfor i=1:1:n  r(i)=(a+b)/2;  if(f(b)*f(r(i))<0)    a=r(i);    else    b=r(i);  endifendforr(n)plot(r)