%Root Finding%Secant Methodclc;clear all;format long;e=10.^(-14);printf("Secant Method\n");a=input("enter a\n") %lower bound of the interval [a,b]b=input("enter b\n") %upper bound of the interval [a,b]n=input("number of iterations\n") %Number of iterationsfor i=1:n  p(i)=b-f(b)*(b-a)/(f(b)-f(a));  if(abs(p(i)-b)<e)    p(i);    break;  else  a=b;  b=p(i);  endif  root=b;endforrootplot(p)